mapper.select:

SELECT SUPPORT_NUMBER, SUPPORT_SPONSER, SUPPORT_RECEIVER, NOVEL_NUMBER, SUPPORT_COIN, SUPPORT_DATE
FROM
    (
    SELECT
    ROWNUM RN, SUPPORT_NUMBER, SUPPORT_SPONSER, SUPPORT_RECEIVER, NOVEL_NUMBER, SUPPORT_COIN, SUPPORT_DATE
    FROM TBL_SUPPORT
    WHERE SUPPORT_SPONSER = #{supportSponser}
    AND ROWNUM <= #{criteria.pageNum} * #{criteria.amount}
    ORDER BY ROWNUM DESC
    )
WHERE RN > (#{criteria.pageNum} - 1) * #{criteria.amount}

SELECT SUPPORT_NUMBER, SUPPORT_SPONSER, SUPPORT_RECEIVER, NOVEL_NUMBER, SUPPORT_COIN, SUPPORT_DATE
FROM TBL_SUPPORT
WHERE NOVEL_NUMBER = #{novelNumber};
